    begin return case
      when status = 'good' and not expired	then	0
      when status = 'pend' and not expired	then	1
      when status = 'good' and expired		then	2
      when status isnull			then	3
      when status = 'pend' and expired		then	4
      else						5 end;
    end;
$$;
