create function mychips.path_find(inp_node text, out_node text = '', size int = 0, max_dep int = 10) returns table (level int,inp text,bot text,bot_seq int,top text,"out" text,top_seq int,ath text[],uuids uuid[],signs int[]) language plpgsql as $$
  declare
